#include "../test_macros.hpp"


SPP_TEST_SHOULD_FAIL_SEMANTIC(
    FloatLiteralAst,
    test_invalid_f8_lower_bound,
    SppFloatOutOfBoundsError, R"(
    fun f() -> std::void::Void {
        let x = -57345.0_f8
    }
)");


